// Helper function to get emojis by category
export function getEmojisByCategory(category: string): FluentEmoji[] {
  return FLUENT_EMOJIS.filter(e => e.category === category);
}

// Helper function to search emojis
export function searchEmojis(query: string): FluentEmoji[] {
  const lowerQuery = query.toLowerCase().trim();
  if (!lowerQuery) return [];

  return FLUENT_EMOJIS.filter(
    e =>
      e.name.toLowerCase().includes(lowerQuery) ||
      e.keywords.some(k => k.toLowerCase().includes(lowerQuery))
  );
}

